class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        ios::sync_with_stdio(false);
        cin.tie(nullptr);
        int n=nums.size();
        long ans[2]={0,0};
        for(int i=n-1; i>=0; --i) {
            if(nums[i]&1) {
                ans[1]=max(ans[1], ans[0]-x)+nums[i];
            } else {
                ans[0]=max(ans[0], ans[1]-x)+nums[i];
            }
        }
        return ans[nums[0]&1];
    }
};
