class Solution {
public:
    vector<vector<long long>>dp;
    long long solve(int j,int i,int n,vector<int>& nums,int x)
    {
        if(j==n)return 0;
        if(dp[j][i]!=-1)return dp[j][i];
        long long ans1=0ll,ans2=0ll;
        if(!((nums[j]&1)^i))
            ans1 = nums[j]+solve(j+1,nums[j]%2,n,nums,x);
        else ans1 = nums[j]-x+solve(j+1,nums[j]%2,n,nums,x);
        ans2 = solve(j+1,i,n,nums,x);
        return dp[j][i]=max(ans1,ans2);
    }

    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();
        dp = vector<vector<long long>>(n+1,vector<long long>(2,-1));
        return nums[0]+solve(1,nums[0]%2,n,nums,x);
    }
};
