        ans2 = solve(j+1,i,n,nums,x);
        return dp[j][i]=max(ans1,ans2);
    }

    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();
        dp = vector<vector<long long>>(n+1,vector<long long>(2,-1));
        return nums[0]+solve(1,nums[0]%2,n,nums,x);
    }
};
