class Solution:    
    def maxScore(self, nums: List[int], x: int) -> int:
        # p = 1 -> odd, p = 0 -> even
        @cache
        def f(i,p):
            if i == len(nums):
                return 0            
            no_take_res = f(i + 1,p)
            curr_p = nums[i] & 1
            if curr_p == p:
                take_res = nums[i] + f(i + 1, curr_p)
            else:
                take_res = nums[i] - x + f(i + 1, curr_p)
            return max(take_res, no_take_res)


        return nums[0] + f(1, nums[0] & 1)
