class Solution {
public:
    long long dp[100005][2];
    long long fun(int ind, int x,vector<int>&v,int pre) {
        if(ind==v.size()) return 0;
        if(dp[ind][pre]!=-1) return dp[ind][pre];
        long long take=-1e12,ntake=-1e12;

        if(v[ind]%2==0) {
            if(pre) take=v[ind]+fun(ind+1,x,v,(v[ind]%2==0));
            else take=v[ind]+fun(ind+1,x,v,(v[ind]%2==0))-x;
        }
        else {
            if(pre) take=v[ind]+fun(ind+1,x,v,(v[ind]%2==0))-x;
            else take=v[ind]+fun(ind+1,x,v,(v[ind]%2==0));
        }
        ntake=fun(ind+1,x,v,pre);
        return dp[ind][pre] = max(take,ntake);
    }
    long long maxScore(vector<int>& v, int x) {
        memset(dp,-1,sizeof(dp));
        return fun(0,x,v,(v[0]%2==0));
    }
};
