class Solution {
public:

    long long f(int i,int state,vector<int>&nums,vector<vector<long long>>&dp,int x){

        if(i==nums.size()) return 0;
        if(dp[i][state]!=-1) return dp[i][state];
       long long  ans1=f(i+1,state,nums,dp,x);
       long long ans2=0;
        if(nums[i]%2==state)
