        for(long d[]:dp) Arrays.fill(d,-1);
        if(nums[0]%2==0) return helper(0,0,nums,x,dp);
        else return helper(0,1,nums,x,dp);
    }

    public long helper(int i,int flag,int[] nums,int x,long[][] dp){  
        if(i>=nums.length)return 0;
        if(dp[i][flag]!=-1)return dp[i][flag];
        long max=0;
        if(flag==0){
            if(nums[i]%2==0){
            max+=nums[i]+helper(i+1,0,nums,x,dp);
        }else{
            max+=Math.max(nums[i]-x+helper(i+1,1,nums,x,dp),helper(i+1,0,nums,x,dp));
        }
        }else{
             if(nums[i]%2!=0){
            max+=nums[i]+helper(i+1,1,nums,x,dp);
        }else{
            max+=Math.max(nums[i]-x+helper(i+1,0,nums,x,dp),helper(i+1,1,nums,x,dp));
        }
        }

        return dp[i][flag]=max;
    }
}
