# Code
```go
func maxScore(nums []int, x int) int64 {
	dp := make([]int, len(nums))
	prevEven, prevOdd := -1, -1
	dp[0] = nums[0]
	if nums[0] % 2 == 0 {
		prevEven = 0
	} else {
		prevOdd = 0
	}
	curScore := dp[0]
	for i := 1; i < len(nums); i++ {
		dp[i] = math.MinInt64
		if nums[i] % 2 == 0 {
			if prevEven != -1 {
				dp[i] = max(dp[i], dp[prevEven]+nums[i])
			}
			if prevOdd != -1 {
				dp[i] = max(dp[i], dp[prevOdd]+nums[i]-x)
			}
			prevEven = i
		} else {
			if prevEven != -1 {
				dp[i] = max(dp[i], dp[prevEven]+nums[i]-x)
			}
			if prevOdd != -1 {
				dp[i] = max(dp[i], dp[prevOdd]+nums[i])
			}
			prevOdd = i
		}
		curScore = max(curScore, dp[i])
	}
	return int64(curScore)
}

func max(values ...int) int {
	maxValue := math.MinInt64
	for _, v := range values {
		if v > maxValue {
			maxValue = v
		}
	}
	return maxValue
}

