class Solution {
public:
    //even=1 odd=0
    long long solve(int ind,vector<int>&nums,int x,int prev,vector<vector<long long>>&dp){
        if(ind==nums.size()) return 0;
        long long pick=0,nonpick=0;
        if(dp[ind][prev]!=-1) return dp[ind][prev];
        if(ind==0){
            pick+=nums[ind]+solve(ind+1,nums,x,prev,dp);
        } 
        else if((prev==1 and nums[ind]%2==0 )or (prev==0 and nums[ind]%2
               !=0)){
            pick+=nums[ind]+solve(ind+1,nums,x,nums[ind]%2==0?1:0,dp);
            nonpick+=solve(ind+1,nums,x,prev,dp);
        }
        else{
            pick+=nums[ind]+(-x)+solve(ind+1,nums,x,nums[ind]%2==0?1:0,dp);
            nonpick+=solve(ind+1,nums,x,prev,dp);
        }
        return dp[ind][prev]=max(pick,nonpick);
    }

    long long maxScore(vector<int>& nums, int x) {
        int n=nums.size();
        vector<vector<long long>>dp(n+1,vector<long long>(2,-1));
        return solve(0,nums,x,nums[0]%2==0?1:0,dp);
    }
};
