                    next++;
                }
                else{
                    int maxi=0;
                    prev++;
                    for(prev;prev<next;prev++){
                        maxi=max(maxi,nums[prev]);
                    }
                    ans=ans+maxi+nums[next]-x;
                    prev=next;
                    next++;
                }
            }
        }
        return ans;
    }
};
