class Solution {
public:
    int rabinKarp(string &text, string &pattern){
        long BASE = 26, MOD = 1e9+7;
        long power = 1;

        for(int i=1; i<pattern.size(); i++){
            power = (power*BASE) % MOD;
        }

         // Find the hash value of the pattern and text
        long pattern_hash = 0;
        long text_hash = 0;

        for(int i=0; i<pattern.size(); i++){
            pattern_hash = (pattern_hash*BASE + (pattern[i]-'a'+1)) % MOD;
            text_hash = (text_hash*BASE + (text[i]-'a'+1)) % MOD;
        }

        if(pattern_hash == text_hash){
            return 0;
        }

        // While moving the window, perform two steps: 
        // 1) Remove the left most character from the window
        // 2) Add the new character to the window

        int start = 0;
        for(int i=pattern.size(); i<text.size(); i++){
            // Step1: Removing left character
            int ch = text[start++]-'a'+1;
            text_hash = text_hash-(ch*power);

            // Step2: Adding new character
            text_hash = (text_hash*BASE + (text[i]-'a'+1))%MOD;

			// To avoid negative hash values
            if(text_hash < 0){
                text_hash += MOD;
            }

			// Hash values of the pattern and text match
            if(text_hash == pattern_hash){
                return start;
            }
        }
		return -1;
    }

    int strStr(string haystack, string needle) {
        if(needle.size() == 0)
            return 0;
        return rabinKarp(haystack, needle);
    }
};
```
