class Solution {
public:
    int strStr(string haystack, string needle) {
        if(needle == "") return 0;
        int n1 = haystack.size();
        int n2 = needle.size();
        int i = 0, j = 0;
        while(i < n1 && j < n2)
        {
            if(haystack[i] == needle[j])
            {
                j++;
            }
            else
            {
                i -= j;
                j = 0;
            }  
            i++;
        }
        if(j == n2) return (i - j);
        else return -1;
    }
};
