class Solution {
public:
    int strStr(string haystack, string needle) {

        if (!needle.size()) {
            return 0;
        }

        for (int i = 0; i < haystack.size(); i++) {
            if (haystack[i] == needle[0]) {
                for (int j = 0; j < needle.size(); j++) {
                    if (haystack[i+j] != needle[j])
                        break;
                }                
                if (j == needle.size())
                    return i;
            }            
        }        
        return -1;
    }
};
