    public int strStr(String haystack, String needle) {
        int p1 = 0;
        int p2 = 0;
        int n1 = haystack.length();
        int n2 = needle.length();

        while (p1 < n1 && p2 < n2) {
            char ch1 = haystack.charAt(p1);
            char ch2 = needle.charAt(p2);            
            if (ch1 == ch2) {
                p1++;
                p2++;
            }
              else {
                p1 = p1 - p2 + 1;  // Reset p1 to the next character in haystack
                p2 = 0;
            }
        }

        if (p2 == n2) {
            return p1 - p2;  // Return the index of the first occurrence of needle
        } else {
            return -1;  // Needle not found
        }
    }
}
