    public int strStr(String haystack, String needle) {
        return ( haystack.indexOf(needle));
    }
}

class Solution {
    public int strStr(String haystack, String needle) {
        if(haystack.contains(needle)){
            int i = haystack.indexOf(needle);
            return i;
        }else{
            return -1;
        }
    }
}

[leetcode](https://leetcode.com)class Solution {
    public int strStr(String haystack, String needle) {
        for(int i=0;i<haystack.length();i++){
            for(int j=i+1;j<=haystack.length();j++){
                if(subString(haystack.substring(i,j),needle)==true)
                     return i;
            }
        }
        return -1;
    }
    public static boolean subString(String str,String s){
        if(str.equals(s))
           return true;
        return false;
    }
}

