```
class Solution {
    private List<String> result;

    public List<String> addOperators(String num, int target) {
        result = new ArrayList<>();
        addOperators(num, target, 0, 0, "", 0);
        return result;
    }

    /* 
    @param: num
    @param: target
    @param: start  start index in num
    @param: val  current value of expr
    @param: expr  expression
    @param: diff  difference
    */
    private void addOperators(String num, int target, int start, long val, String expr, long diff) {
        if (start == num.length()) {

            if (target == val) {
                result.add(expr);
            } 
            return;
        }

        for (int end = start + 1; end <= num.length(); end++) {
            String cur = num.substring(start, end);

            if (cur.charAt(0) == '0' && cur.length() > 1) {
                // Invalid number
                continue;
            }

            long curNum = Long.parseLong(cur);

            if (start == 0) {
                // The first number
                addOperators(num, target, end, curNum, cur, curNum);
            } else {
                // + in front
                addOperators(num, target, end, val + curNum, expr + "+" + cur, curNum);

                // - in front
                addOperators(num, target, end, val - curNum, expr + "-" + cur, -curNum);

                // * in front
                addOperators(num, target, end, val - diff + diff * curNum, expr + "*" + cur, diff * curNum);
            }
        }
    }
}
