The following DFS is similar to top solutions.
* c++
```
    vector<string> addOperators(string num, int target) {
        vector<string> exp;
        string cur;
        dfs(0,0,0,target,cur,num,exp);
        return exp;
    }
    void dfs(int p, int prod, long res, int target, string& cur, string &num,vector<string>& exp) {
        int n = num.size();
        if(p==n && res==target) exp.push_back(cur);
        long v = 0;
        int sz = cur.size(); 
        string s;
        for(int i=p;i<n;i++) {
            v=v*10+num[i]-'0';
            s+=num[i];
            if(cur.empty()) dfs(i+1,v,res+v,target,cur+=s,num,exp);
            else {
                dfs(i+1,v,res+v,target,cur+='+'+s,num,exp);
                cur.resize(sz);
                dfs(i+1,-v,res-v,target,cur+='-'+s,num,exp);
                cur.resize(sz);
                dfs(i+1,prod*v,res-prod+prod*v,target,cur+='*'+s,num,exp);
            }
            cur.resize(sz);
            if(!v) return;
        }
    }
``` 
* java
```
	List<String> res;
    public List<String> addOperators(String num, int target) {
        res=new ArrayList<>();
        dfs(0,0,0,target,num,new StringBuilder());
        return res;
    }
    private void dfs(int p, long eval, long mult, int target, String num, StringBuilder sb) {
        int n=num.length();
        if(p==n) {
            if(eval==target) {
                res.add(sb.toString());
            }
        }
        int len=sb.length();
        long number=0;
        for(int i=p;i<n;i++) {
            number = number*10 + num.charAt(i)-'0';
            if(p==0) {//no sign before first number
                dfs(i+1,number,number,target,num,sb.append(number));
                sb.setLength(len);
            } else {
                dfs(i+1,eval+number,number,target,num,sb.append('+').append(number));
                sb.setLength(len);
                dfs(i+1,eval-number,-number,target,num,sb.append('-').append(number));
                sb.setLength(len);
                dfs(i+1,eval-mult+mult*number,mult*number,target,num,sb.append('*').append(number));
                sb.setLength(len);
            }
            if(num.charAt(p)=='0') { //01 is invalid
                return;
            }
        }
    }
