public class Solution 
{

    private List<string> answers;
    private string num;
    private int target;

    public IList<string> AddOperators(string num, int target) {        
        if (num == null || num.Length == 0)
            return new List<string>();

        // class member usage for readability
        this.num = num;
        this.target = target;
        this.answers = new List<string>();        

        process(0, 0, 0, 0, new StringBuilder());

        return answers;
    }

    // Retrieving answers during recursive procedure call
    // Type long is used to avoid integer overflows
    // StringBuilder is used to speed up the execution and reduce memory consumption
    private void process(int index, long previousOperand, long currentOperand, long value, StringBuilder expression)
    {
        if (index == num.Length)
        {
            // new answer found
            // value 0 is used for expression validation
            if (value == target && currentOperand == 0)
                answers.Add(expression.ToString());
            return;
        }

        // Case 0 - create multi-digit number
        currentOperand = currentOperand * 10 + int.Parse(num[index].ToString());
        string sop = currentOperand.ToString();

        if (currentOperand > 0)
        {
            // Case 1 - proceed with no operation only if it does not start with 0 e.g. 05
            process(index + 1, previousOperand, currentOperand, value, expression);
        }

        if (expression.Length == 0)
        {
            // Case 2 - proceed with first number
            process(index + 1, currentOperand, 0, currentOperand, expression.Append(sop));
            expression.Length = expression.Length - sop.Length;
        }
        else
        {
            // Case 3 - proceed with addition
            process(index + 1, currentOperand, 0, value + currentOperand, expression.Append("+").Append(sop));
            expression.Length = expression.Length - sop.Length - 1;

            // Case 4 - proceed with subtraction
            process(index + 1, -currentOperand, 0, value - currentOperand, expression.Append("-").Append(sop));
            expression.Length = expression.Length - sop.Length - 1;

            // Case 5 - proceed with multiplication
            process(index + 1, previousOperand * currentOperand, 0, value - previousOperand + (previousOperand * currentOperand), expression.Append("*").Append(sop));            
            expression.Length = expression.Length - sop.Length - 1;
        }
    }
}

