class Solution {
public:
    vector<string> addOperators(string num, int target) {
        addOperatorsAndEvaluate(num, target, 0, "", 0, 0);
        return expressions;
    }

private:
    vector<string> expressions;
    void addOperatorsAndEvaluate(string& nums, int& target, int index, string exp, long value, long prev) {
        if (index==nums.size()) {
            if (value == target) {
                expressions.push_back(exp);
            }
            return;
        }

        string numStr = "";
        long num = 0;
        for(int j=index; j<nums.size(); j++) {
            // Handling the case for avoiding numbers with leading 0
            if (j>index && nums[index]=='0') break;

            numStr += nums[j];
            num = num*10 + (nums[j]-'0');

            if (exp.size()==0) {
                addOperatorsAndEvaluate(nums, target, j+1, exp+numStr, num, num);
            } else {
                addOperatorsAndEvaluate(nums, target, j+1, exp+'+'+numStr, value+num, num);
                addOperatorsAndEvaluate(nums, target, j+1, exp+'-'+numStr, value-num, -num);
                addOperatorsAndEvaluate(nums, target, j+1, exp+'*'+numStr, (value-prev)+(prev*num), prev*num);
            }
        }
    }
};
