class Solution {
public:
    void dfs(const string& s, int idx, long long curr, long long val, int target, string& expr, vector<string>& res) {
        if (idx == s.size()) {
            if (curr == target) {
                res.push_back(expr);
            }
            return;
        }

        size_t sz = expr.size();
        long long int v = 0;
        for (int i = idx; i < s.size(); ++i) {
            v = v * 10 + s[i] - '0';

            string&& sub = s.substr(idx, i - idx + 1);
            if (idx == 0) {
                expr += sub;
                dfs(s, i + 1, curr + v, v, target, expr, res);
                expr.resize(sz);
            } else {
                expr += '+';
                expr += sub;
                dfs(s, i + 1, curr + v, v, target, expr, res);
                expr.resize(sz);

                expr += '-';
                expr += sub;
                dfs(s, i + 1, curr - v, -v, target, expr, res);
                expr.resize(sz);

                expr += '*';
                expr += sub;
                dfs(s, i + 1, curr - val + v * val, v * val, target, expr, res);
                expr.resize(sz);
            }

            if (!v) {
                break;
            }
        }
    }

    vector<string> addOperators(string num, int target) {
        string expr;
        vector<string> res;
        dfs(num, 0, 0, 0, target, expr, res);
        return res;
    }
};
