		# Time O(4^n), Space O(n)
		def backtrack(idx, prev,curr,value,s):
			if idx == n:
				if value == target and curr == 0:
					self.result.append(s)
				return

			curr = curr * 10 + int(num[idx])

			if curr > 0:
