class Solution {
public:
    vector<string> result;

    vector<string> addOperators(string &num, int target) {
        dfs(num, target, 0, "", 0, 0);
        return result;
    }

    void dfs(string &num, int target, int start, string str, long long sum, long long last) {
        if (num.size() == start) {
            if (target == sum) result.push_back(str);
            return;
        }

        long long x = 0;
        string ret = "";

        for (int i = start; i < num.size(); ++i) {
            x = x * 10 + num[i] - '0';
            ret += num[i];

            if (start == 0) {
                dfs(num, target, i + 1, str + ret, x, x);
            } else {
                dfs(num, target, i + 1, str + "*" + ret, sum - last + x * last, x * last);
                dfs(num, target, i + 1, str + "+" + ret, sum + x, x);
                dfs(num, target, i + 1, str + "-" + ret, sum - x, -x);
            }

            if (x == 0) break;            
        }
    }
};
