                        q.append([remainstr[j:], fstr + '+' + val, fval + int(val), int(val)])
                        q.append([remainstr[j:], fstr + '-' + val, fval - int(val), -int(val)])
                        q.append([remainstr[j:], fstr + '*' + val, fval - flast + flast*int(val), flast*int(val)])
        return res

    # dfs
    def addOperators(self, num: str, target: int) -> List[str]:
        res = []
        self.target = target

        for i in range(1, len(num) + 1):
            if i==1 or (i>1 and num[0] != '0'):
                self.dfs(num[i:], num[:i], int(num[:i]), int(num[:i]), res)
        return res

    def dfs(self, num, fstr, fval, flast, res):
        # fstr = string of current formula
        # fval = value of current formula
        # flast = last value for +- and last computing result for * in formula. For example, if fstr=2+3, then flast=3, if fstr=2-3, then flast=-3, if fstr=2+3*4, then flast=3*4=12
        if not num:
            if fval == self.target:
                res.append(fstr)
            return

        for i in range(1, len(num)+1):
            val=num[:i]
            if i == 1 or (i>1 and num[0] != '0'):
                self.dfs(num[i:], fstr + '+' + val, fval + int(val), int(val), res)
                self.dfs(num[i:], fstr + '-' + val, fval - int(val), -int(val), res)
                self.dfs(num[i:], fstr + '*' + val, fval-flast+flast*int(val), flast*int(val), res)

            # 2+3*2: currently coming is *2, current formula = 2+3, its value is 5, the last vale of this formula is 3, thus: 5-3+3*2 is fine
            # 2-3*5: currently coming is *5, current formula = 2-3, its value is -1, the last vale of this formula is -3, thus: -1-(-3) + (-3)*5 = -1+3-15=-13
            # 3*5*2: currently coming is *2, current formula = 3*5, its value is 15, the last vale of this formula is 3*5=15!, thus: 15-15+15*2=30, result is okay as well
```

