            else:
                self.helper(string, end, target, sum_so_far + cur, cur, path + "+" + sub_string, results)
                self.helper(string, end, target, sum_so_far - cur, -cur, path + "-" + sub_string, results)
                self.helper(string, end, target, sum_so_far - last + cur * last, cur * last, path + "*" + sub_string, results)

