    public class Solution {
    List<string> result;
    string s;
    int target;
	// inspired from this post https://leetcode.com/problems/expression-add-operators/discuss/1553905/C-DFS-Easy-to-understand
    public IList<string> AddOperators(string num, int target) 
    {
        result = new List<string>();
        this.s=num;
        this.target=target;

        dfs(0, string.Empty, 0, 0);
        return result;
    }

    public void dfs(int startIndex, string temp, long prev, long curResult)
    {
        if(startIndex == s.Length)
        {
            if(prev+curResult == target)
                result.Add(temp);  
            return;
        }

        // start from startIndex again for next number
        for(int i=startIndex; i < s.Length; i++)
        {
            // i+1 - startIndex was length from startIndex
            // get every substring from startIndex
            var cur = s.Substring(startIndex, i+1-startIndex);

            // leading 0
            if(cur.Length > 1 && cur[0] == '0')
                continue;

            var curNum = Int64.Parse(cur);

            if(startIndex == 0)
                dfs(i+1, cur, curNum, 0);
            else
            {
                // please refer to 227. Basic Calculator II's optimal solution about defered calculation 
                    //  '*')
                        dfs(i+1, temp + "*" + cur, prev*curNum, curResult);
                    // '+')
                        dfs(i+1, temp + "+" + cur, curNum,  curResult + prev);
                    // '-')
                        dfs(i+1, temp + "-" + cur, -curNum, curResult + prev);
            }
        }
    }
}

