class Solution:
    def addOperators(self, num: str, target: int) -> List[str]:
        result = []
        self.add_helper(num, 0, "", 0, target, result, 0)
        return result

    def add_helper(self, num, cur_idx, candidate, value, target, result, last):
	    # add to result when proceeding the whole string and value equals to target
        if value == target and cur_idx == len(num):
            result.append(candidate)
            return

        # slice a number between current position to the end of string
        for i in range(cur_idx, len(num)):
            # if number is not a single digit 0 (0 at current index but have numbers in the following), just dont slice any more
            if num[cur_idx] == "0" and i != cur_idx:
                break
            cur_num = num[cur_idx : i + 1]

            # if it's slicing number from beginning
            if cur_idx == 0:
                self.add_helper(num, i + 1, cur_num, value + int(cur_num), target, result, int(cur_num))
            else:
                # use last to record last number proceeded
                self.add_helper(num, i + 1, candidate + "+" + cur_num, value + int(cur_num), target, result, int(cur_num))
                self.add_helper(num, i + 1, candidate + "-" + cur_num, value - int(cur_num), target, result, -int(cur_num))                
                self.add_helper(num, i + 1, candidate + "*" + cur_num, value - last + last * int(cur_num), target, result, last * int(cur_num))


