class Solution:
    def addOperators(self, num: str, target: int) -> List[str]:

        ans = []

        def backtrack(start, path):
            if len(path) == 1 and path == "0":
                return
            if len(path) >= 2 and path[-2:] in ["+0", "-0", "*0"]:
                return
            if start == len(num) - 1:
                if eval(path+num[-1]) == target:
                    ans.append(path+num[-1])
                return
            backtrack(start+1, path+num[start]+"*")
            backtrack(start+1, path+num[start]+"+")
            backtrack(start+1, path+num[start]+"-")
            backtrack(start+1, path+num[start])


        backtrack(0, "")

        return ans


