Got the idea for calculating the value on the fly from 
https://leetcode.com/problems/expression-add-operators/discuss/441680/Accepted-C-backtracking-solution%3A-Easy-to-understand-w-comments-fast-93.14-memory-100

```
public class Solution 
{
    List<char> ops;
    IList<string> lst;

    public IList<string> AddOperators(string num, int target) 
    {
        ops = new List<char>();
        lst = new List<string>();
        ops.Add('+');
        ops.Add('-');
        ops.Add('*');
        dfs(num, 0, "", 0, 0, target);
        return lst;
    }

    public void dfs(string s, int index, string calc, long prev, long sofar, int target)
    {
        if(index == s.Length)
        {
            if(sofar == target)
                lst.Add(calc);

            return;
        }

        int count = 1;
        for(int i = index; i < s.Length; i++)
        {
            var nextindex = index + count;
            var cur = s.Substring(index, count);

            if(cur.Length > 1 && cur[0] == '0')
                continue;

            var curnum = Convert.ToInt64(cur);

            if(index == 0)
            {
                dfs(s, nextindex, cur, curnum, curnum, target);
            }
            else
            {
                foreach(var op in ops)
                {
                    if(op == '*')
                        dfs(s, nextindex, calc + "*" + cur, curnum*prev, ((sofar - prev) + prev * curnum), target);
                    else if(op == '+')
                        dfs(s, nextindex, calc + "+" + cur, curnum, sofar + curnum, target);
                    else if(op == '-')
                        dfs(s, nextindex, calc + "-" + cur, -curnum, sofar - curnum, target);
                } 
            }

            count++;
        }
    }
}
