class Solution:
    def addOperators(self, num: str, target: int) -> List[str]:

        def backtrack(index,stack):

            if index == len(num)-1:
                stack.append(num[index])
                expres = "".join(stack[:])
                if eval(expres) == target:
                    out.append(expres)
                return

            leadZero = False

            if not (stack and stack[-1].isdigit()) and num[index] == '0':

                leadZero = True

            stack.append(num[index])

            for op in ["+","-","*"]:
                stack.append(op)  
                backtrack(index+1,stack[:])
                stack.pop()

            if not leadZero:

                backtrack(index+1,stack[:])

        out = []

        backtrack(0,[])

        return out
