class Solution:
    def addOperators(self, num: str, target: int) -> List[str]:
        results = []
        n = len(num)

        def dfs(i: int, expression: str, value: int, last: Optional[int]):
            if i == n and value == target:
                results.append(expression)

            for j in range(i+1, n+1):
                itoj = num[i:j]
                curr = int(itoj)
                if j == i + 1 or not itoj.startswith('0'):
                    if last is None:
                        dfs(j, itoj, curr, curr)
                    else:
                        dfs(j, f'{expression}+{itoj}', value + curr, curr)
                        dfs(j, f'{expression}-{itoj}', value - curr, -curr)
                        dfs(j, f'{expression}*{itoj}', value - last + last * curr, last * curr)

        dfs(0, '', 0, None)
        return results
