class Solution:
    def addOperators(self, num: str, target: int) -> List[str]:
        result = []
        n = len(num)

        def dfs(pos, path, val, last_operand):
            if pos == n:
                if val == target:
                    result.append(path)
            else:
                for i in range(pos, n):
                    # try [pos..i] as an operand
                    if num[pos] == '0' and i != pos:
                        # For example "0555", Only consider "0" as possible operand
                        break
                    s = num[pos : i + 1]
                    v = int(s)
                    if pos == 0:
                        # no possible left operand
                        dfs(i + 1, s, v, v)
                    else:
                        dfs(i + 1, path + '+' + s, val + v, v)
                        dfs(i + 1, path + '-' + s, val - v, -v)
