void moveZeroes(int* nums, int numsSize) {
    int totalZeros = 0;

    for(int i = 0; i < numsSize; i++) {
        if(nums[i] == 0) {
            totalZeros++;
        }
        else {
            nums[i - totalZeros] = nums[i];
        }
    }

    for(int i = (numsSize - totalZeros); i < numsSize; i++) {
        nums[i] = 0;
    }
}

