//Solution 01:
class Solution {
public:
    void moveZeroes(vector<int>& nums) {
        int n= nums.size();
        int i=0, j=0;

        while(i<n){
            if(nums[i]!=0){
                swap(nums[i], nums[j]);
                i++; j++;
            }
            else 
                i++;
        }
    }
};

//Solution 02:
class Solution {
public:
    void moveZeroes(vector<int>& nums) {
        int n= nums.size();
        queue<int> add;
        for(int i=0; i<n; i++){
            if(nums[i]==0)
                add.push(i);
            else{
                if(!add.empty()){
                    int x=add.front();
                    nums[x]=nums[i];
                    nums[i]=0;
                    add.push(i);
                    add.pop();
                }
            }
        }
    }
};
```

