class Solution(object):
    def moveZeroes(self, nums):

		# Set slow pointer to the 1st zero.
        for left in range(len(nums)):
            if nums[left] == 0:
                break

		# Swap elements at fast & slow if fast points to a non-zero number (and increment).
        for right in range(left + 1, len(nums)):
            if nums[right] != 0:
                nums[left], nums[right] = nums[right], nums[left]
                left += 1

