// peek - if(movedNext) -> return  current 
// peek - if(!movedNext) - moveNext and return current; set movedNext to true;
// hasNext - if(movedNext) - return true;
// hasNext - if(!movedNext) - moveNext, return true and set MoveNext to true;
// Next - if(movedNext) -set MoveNext to false and return current;
// Next - if(!movedNext) - setMoveNext to false,  moveNext and  return current;

class PeekingIterator {
    IEnumerator<int> _iterator;
    bool movedNext = false;

    // iterators refers to the first element of the array.
    public PeekingIterator(IEnumerator<int> iterator) 
    {
       _iterator = iterator;
        movedNext = true;
    }

    // Returns the next element in the iteration without advancing the iterator.
    public int Peek() 
    {
        if(movedNext)
            return _iterator.Current;

        movedNext = _iterator.MoveNext();
        return _iterator.Current;
    }

    // Returns the next element in the iteration and advances the iterator.
    public int Next() 
    {        
      if(movedNext)
      {
          movedNext = false;
          return _iterator.Current;         
      }

     _iterator.MoveNext();
      movedNext = false;
      return _iterator.Current;       
    }

    // Returns false if the iterator is refering to the end of the array of true otherwise.
    public bool HasNext() 
    {
         if(movedNext)
            return true;      
         movedNext = _iterator.MoveNext();
