    public int Peek() 
    {
        if(movedNext)
            return _iterator.Current;

        movedNext = _iterator.MoveNext();
        return _iterator.Current;
    }

    // Returns the next element in the iteration and advances the iterator.
    public int Next() 
    {        
      if(movedNext)
      {
          movedNext = false;
          return _iterator.Current;         
      }

     _iterator.MoveNext();
      movedNext = false;
      return _iterator.Current;       
    }

    // Returns false if the iterator is refering to the end of the array of true otherwise.
    public bool HasNext() 
    {
         if(movedNext)
            return true;      
         movedNext = _iterator.MoveNext();
         return movedNext;		       
    }
