#include <optional>
class PeekingIterator : public Iterator {
private:
    optional<int> next_;
public:
	PeekingIterator(const vector<int>& nums) : Iterator(nums) {
	    if (Iterator::hasNext()) {
            next_ = Iterator::next();
        }
	}

	int peek() const {
        return *next_;
	}

	int next() {
	    auto ret = *next_;
        if (Iterator::hasNext()) {
            next_ = Iterator::next();
        } else {
            next_.reset();
        }
        return ret;
	}

	bool hasNext() const {
	    return next_.has_value();
	}
};
```

