class PeekingIterator {

    private var it: IndexingIterator<Array<Int>>
    private var buf: Int?

    init(_ arr: IndexingIterator<Array<Int>>) {
        self.it = arr
        self.buf = it.next()
    }

    private func _next() -> Int {
        self.buf ?? 0
    }

    func next() -> Int {
        defer { self.buf = it.next() }
        return self._next() 
    }

    func peek() -> Int {
        return self._next() 
    }

    func hasNext() -> Bool {
        self.buf != nil
    }

}
