class PeekingIterator : public Iterator {
public:
    queue<int> q;

	PeekingIterator(const vector<int>& nums) : Iterator(nums) {
	    for(int i=0 ; i<nums.size() ; i++)
            q.push(nums[i]);
	}

	int peek() {
        return q.front();    
	}

	int next() {
	    int val = q.front();
        q.pop();
        return val;
	}

	bool hasNext() const {
	    if(q.size())
            return true;

        return false;
	}
};
