        for (int i = 0, n = 0; i < nums.size(); i++) {
            int j = !i || nums[i] != nums[i - 1] ? 0 : n;
            n = subs.size();
            for (; j < n; j++) {
                subs.push_back(subs[j]);
                subs.back().push_back(nums[i]);
            }
        }
        return subs;
    }
