class Solution {
public:
    vector<vector<int>>ans;
    void subsequence(int ind,int n,vector<int>&arr,vector<int>&ds){
    if(ind==n){
        ans.push_back(ds);
        return;
    }
 // Since in this question we can change the order of the array elements unlike question(https://leetcode.com/problems/non-decreasing-subsequences/description/).
    //  Therefore we sorted the nums vector initially.
    //  Therefore condition , if(ds.size()==0 || nums[index] >= ds[ds.size()-1]) is not required .
    ds.push_back(arr[ind]);
    subsequence(ind+1,n,arr,ds);
    ds.pop_back();
    if(ds.size()==0 || arr[ind]!=ds[ds.size()-1])subsequence(ind+1,n,arr,ds);
}
    vector<vector<int>> subsetsWithDup(vector<int>& nums) {
        sort(nums.begin(),nums.end());
        vector<int>ds;
        subsequence(0,nums.size(),nums,ds);
        return ans;
    }
};
