class Solution {
    public List<List<Integer>> subsetsWithDup(int[] nums) {
        HashSet<List<Integer>> res = new HashSet<>(); // No need to add duplicate subsets so hashset is used
        backtrack(nums,res,new ArrayList<>(),0); 
        List<List<Integer>> ans = res.stream().map(i->i).collect(Collectors.toList()); // convert hashset to list
        return ans;
    }
    public void backtrack(int[] nums,HashSet<List<Integer>> res,List<Integer> list,int idx){
        if(idx == nums.length){
            List<Integer> l = new ArrayList<>(list); // new list so the orignal list does no get changed
            Collections.sort(l); // sorting the subsets 
            res.add(l);
            return;
        }
        list.add(nums[idx]);  // number gets picked
        backtrack(nums,res,list,idx+1);
        list.remove(list.size()-1); // number does not get picked
        backtrack(nums,res,list,idx+1);
    }
}
