                    // Now, suppose we have a subset x, which including the second 1 but not the first 1, x is a duplicate.
                    if (j==0 || (j>0 && (nums[j] != nums[j-1] || (1 & (i>>(j-1))) == 1))) {
                        set.add(nums[j]);
                    } else {
                        duplicateSet = true;
                        break;
                    }
                }
            }
            if (!duplicateSet) {
