class Solution {
public:
    vector<vector<int>> subsetsWithDup(vector<int>& nums) {
        int n = nums.size();
        int s = 1 << n;
        set<multiset<int>> ans;
        for(int i = 0; i < s; i++){
            multiset<int> temp;
            for(int j = 0; j < n; j++){
                if(i & (1 << j))
                    temp.insert(nums[j]);
            }
            ans.insert(temp);
        }
        vector<vector<int>> res;
        for(auto i: ans){
            multiset<int> temp = i;
            vector<int> set_to_vec{temp.begin(), temp.end()};
            res.push_back(set_to_vec);
        }
        return res;
    }
};
```

