    void backTrack(vector<vector<int>> &ans, vector<int> &nums, vector<int> &curr, int index) {
    ans.push_back(curr);
    for(int i = index; i < nums.size(); i++) {
        if(i != index && nums[i] == nums[i-1]) continue;
        curr.push_back(nums[i]);
        backTrack(ans, nums, curr, i+1);
        curr.pop_back();
    }
