class Solution {
public:
    vector<vector<int>> ans;
    vector<vector<int>> subsetsWithDup(vector<int>& nums) {
        sort(nums.begin(),nums.end());
        vector<int> curr;
        helper(nums,0,curr);
        return ans;
    }
    void helper(vector<int> &nums,int i,vector<int> curr){
        if(i==nums.size()){
            ans.push_back(curr);
            return;
        }
        int j=i;
        while(j<nums.size()&&nums[j]==nums[i]) j++;
        helper(nums,j,curr);
        for(int x=i;x<j;x++){
            curr.push_back(nums[i]);
            helper(nums,j,curr);
        }
    }
};
