        for(int index = 0 ; index < set ; index++)
        { 
            vector<int> cases;
            for(int j = 0 ; j < size ; j++)
            {
                if(index &( 1 << j))
                    cases.push_back(nums[j]);
            }
            if(!exist(res,cases))
             res.push_back(cases);
        }
        return res;
    }
