# Code
```
class Solution {
public:
    // Define a custom hash function for vector keys
    struct vectorkey { 
        size_t operator()(const vector<int>& v) const { 
            size_t hash = 0; 
            for (auto x : v) { 
                hash ^= std::hash<int>{}(x); 
            }
            return hash; 
        }
    };

    // Use a map to store unique subsets and their counts
    unordered_map<vector<int>, int, vectorkey> map1;

    // Function to generate subsets with handling duplicates
    void creator(vector<int> temp, int index, vector<int> nums) {
        if (temp.size() != 0) {
            sort(temp.begin(), temp.end()); // Sort the subset elements
            map1[temp]++; // Store the sorted subset in the map
        }
        for (int i = index; i < nums.size(); i++) {
            temp.push_back(nums[i]); // Include the current element
            creator(temp, i + 1, nums); // Generate subsets without current element
            temp.pop_back(); // Backtrack and remove the current element
        }
    }

    // Generate unique subsets with duplicates removed
    vector<vector<int>> subsetsWithDup(vector<int>& nums) {
        creator({}, 0, nums); // Start generating subsets
        vector<vector<int>> final; 
        for (auto it : map1) { 
            final.push_back(it.first); // Add each unique subset to the result
        }
        final.push_back({}); // Include an empty subset
        return final;
    }
};


```

