func subsetsWithDup(_ nums: [Int]) -> [[Int]] {
	var q = [[Int]]()
	q.append([])
	var count = q.count
	var nums = nums.sorted() // Simplifies skipping duplicates
	for (i, num) in nums.enumerated() {
		let start = (i > 0 && nums[i-1] == num) ? count : 0
		count = q.count
		for j in start ..< count {
			q.append( q[j] + [num] )
		}
	}
	return q
}
