                    chToWordMap[currCh] = currWord;
                    wordToChMap[currWord] = currCh;
                }
                //=================================================
                currWord = "";
            }
            else currWord.push_back(s[i]);
        }
        //===============================================================================================
        return (chIdx == pattern.size());

    }
};
